SELECT cust_id, cust_nm, phone, email, address1, address2, zip, gender, birthday, status, grade, reg_dtm, upd_dtm, tot_buy_amt FROM custom WHERE grade IN ('N','S','G','V','A')
AND cust_id LIKE #{keyword}
AND cust_nm LIKE #{keyword}
ORDER BY (grade='A') DESC, reg_dtm DESC
